import { ORDER_BURGER } from "./BurgerTypes"

const initialState = {
    burgerBase: 100
}

const burgerReducer = (state=initialState, action=> {
    switch(action.type) {
        case ORDER_BURGER:
            return{
                ...state,
                burgerBase:state.burgerBase-1
            }
        default:
            return state
    }
})
export default burgerReducer;
